    0x20, 0x40, 0x14, 0x24, 0x08, 0x18, 0x06, 0x00,
    0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
};

/// All available character ranges
pub const ranges = [_]CharRange{
    .{ .start = 0x0000, .end = 0x007F, .data = &basic_latin },
    .{ .start = 0x00A0, .end = 0x00FF, .data = &extended_latin },
    .{ .start = 0x2500, .end = 0x257F, .data = &box_drawing },
    .{ .start = 0x2580, .end = 0x259F, .data = &block_elements },
    .{ .start = 0x0390, .end = 0x03C9, .data = &greek },
    .{ .start = 0x3040, .end = 0x309F, .data = &hiragana },
};

/// Find character data for a given codepoint
pub fn findCharData(codepoint: u21) ?struct { data: []const u8, offset: usize } {
    for (ranges) |range| {
        if (codepoint >= range.start and codepoint <= range.end) {
            const index = codepoint - range.start;
            const offset = index * 8;
            if (offset + 8 <= range.data.len) {
                return .{
                    .data = range.data[offset .. offset + 8],
                    .offset = offset,
                };
            }
        }
    }
    return null;
}
